mapper.select:
SELECT

FROM
PL_SYS_VERIFICATION
WHERE
IS_DELETED=0
AND CODE=#{entity.code}
AND OBJECT=#{entity.object}
AND INACTIVE_DATE>#{entity.inactiveDate}
AND TYPE=#{entity.type}

SELECT

FROM
PL_SYS_VERIFICATION
WHERE
IS_DELETED=0
AND CODE=#{entity.code}
AND ID=#{entity.id}
AND UNIX_TIMESTAMP(INACTIVE_DATE)>UNIX_TIMESTAMP(#{entity.inactiveDate})
AND TYPE=#{entity.type}
